// Program to demonstrate star pattern
package org.tnsif.looping;

public class ForLoopExecutor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int n = 4;
for (int i =1 ; i<=n;i++)
{
	for(int j =1; j<=i;j++)
	{
		System.out.print("*");
	}
	System.out.println();

}
	}

}
